import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Box, 
  Typography, 
  Paper, 
  Tabs, 
  Tab,
  CircularProgress
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';

// Import components
import FileUpload from './components/FileUpload';
import QueryInput from './components/QueryInput';
import ResultDisplay from './components/ResultDisplay';
import FileList from './components/FileList';

// Create theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f5f5f5',
    },
  },
});

function App() {
  // State
  const [sessionId, setSessionId] = useState('');
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [queryResult, setQueryResult] = useState(null);
  const [isQuerying, setIsQuerying] = useState(false);
  const [activeTab, setActiveTab] = useState(0);
  const [querySource, setQuerySource] = useState('both'); // 'uploaded', 'online', or 'both'

  // Initialize session
  useEffect(() => {
    // Generate a random session ID if not already set
    if (!sessionId) {
      const newSessionId = Math.random().toString(36).substring(2, 15);
      setSessionId(newSessionId);
      
      // Store in localStorage to persist across page refreshes
      localStorage.setItem('researchAssistantSessionId', newSessionId);
    }
    
    // Clean up on unmount
    return () => {
      // Optional: clear session on unmount
      // clearSession();
    };
  }, [sessionId]);

  // Load session from localStorage
  useEffect(() => {
    const storedSessionId = localStorage.getItem('researchAssistantSessionId');
    if (storedSessionId) {
      setSessionId(storedSessionId);
      
      // Fetch uploaded files for this session
      fetchUploadedFiles(storedSessionId);
    }
  }, []);

  // Fetch uploaded files
  const fetchUploadedFiles = async (sid) => {
    try {
      const response = await fetch(`/files?session_id=${sid}`);
      const data = await response.json();
      
      if (data.files) {
        setUploadedFiles(data.files);
      }
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  };

  // Handle file upload
  const handleFileUpload = async (file) => {
    setIsUploading(true);
    setUploadProgress(0);
    
    const formData = new FormData();
    formData.append('file', file);
    
    try {
      // Simulate progress
      const progressInterval = setInterval(() => {
        setUploadProgress((prev) => {
          const newProgress = prev + 10;
          return newProgress >= 90 ? 90 : newProgress;
        });
      }, 300);
      
      const response = await fetch(`/upload?session_id=${sessionId}`, {
        method: 'POST',
        body: formData,
      });
      
      clearInterval(progressInterval);
      setUploadProgress(100);
      
      const data = await response.json();
      
      // Refresh file list
      fetchUploadedFiles(sessionId);
      
      setTimeout(() => {
        setIsUploading(false);
        setUploadProgress(0);
      }, 500);
    } catch (error) {
      console.error('Error uploading file:', error);
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  // Handle query submission
  const handleQuerySubmit = async (query) => {
    setIsQuerying(true);
    setQueryResult(null);
    
    const formData = new FormData();
    formData.append('query', query);
    formData.append('source', querySource);
    
    try {
      const response = await fetch(`/query?session_id=${sessionId}`, {
        method: 'POST',
        body: formData,
      });
      
      const data = await response.json();
      setQueryResult(data);
    } catch (error) {
      console.error('Error submitting query:', error);
      setQueryResult({
        error: 'An error occurred while processing your query. Please try again.'
      });
    } finally {
      setIsQuerying(false);
    }
  };

  // Handle tab change
  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    
    // Set query source based on tab
    if (newValue === 0) {
      setQuerySource('both');
    } else if (newValue === 1) {
      setQuerySource('uploaded');
    } else if (newValue === 2) {
      setQuerySource('online');
    }
  };

  // Clear session
  const clearSession = async () => {
    try {
      await fetch(`/session?session_id=${sessionId}`, {
        method: 'DELETE',
      });
      
      setUploadedFiles([]);
      setQueryResult(null);
      
      // Generate a new session ID
      const newSessionId = Math.random().toString(36).substring(2, 15);
      setSessionId(newSessionId);
      localStorage.setItem('researchAssistantSessionId', newSessionId);
    } catch (error) {
      console.error('Error clearing session:', error);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            Research Assistant
          </Typography>
          
          <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
            <Typography variant="h5" component="h2" gutterBottom>
              Upload Documents
            </Typography>
            <FileUpload 
              onUpload={handleFileUpload} 
              isUploading={isUploading} 
              progress={uploadProgress} 
            />
            
            {uploadedFiles.length > 0 && (
              <Box sx={{ mt: 2 }}>
                <FileList files={uploadedFiles} />
              </Box>
            )}
          </Paper>
          
          <Paper elevation={3} sx={{ p: 3 }}>
            <Tabs 
              value={activeTab} 
              onChange={handleTabChange} 
              centered
              sx={{ mb: 2 }}
            >
              <Tab label="Search All Sources" />
              <Tab label="Search Uploaded Documents" />
              <Tab label="Search Online Literature" />
            </Tabs>
            
            <QueryInput 
              onSubmit={handleQuerySubmit} 
              disabled={isQuerying || (activeTab === 1 && uploadedFiles.length === 0)} 
            />
            
            {isQuerying && (
              <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
                <CircularProgress />
              </Box>
            )}
            
            {queryResult && !isQuerying && (
              <Box sx={{ mt: 3 }}>
                <ResultDisplay result={queryResult} />
              </Box>
            )}
          </Paper>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
