import React, { useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { 
  Box, 
  Typography, 
  Paper, 
  LinearProgress,
  Alert
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';

const FileUpload = ({ onUpload, isUploading, progress }) => {
  const [error, setError] = React.useState('');
  
  const onDrop = useCallback((acceptedFiles) => {
    // Reset error
    setError('');
    
    // Check file type
    const file = acceptedFiles[0];
    if (!file) return;
    
    const allowedTypes = ['.pdf', '.docx', '.txt'];
    const fileExtension = file.name.substring(file.name.lastIndexOf('.')).toLowerCase();
    
    if (!allowedTypes.includes(fileExtension)) {
      setError(`Unsupported file type. Allowed types: ${allowedTypes.join(', ')}`);
      return;
    }
    
    // Upload file
    onUpload(file);
  }, [onUpload]);
  
  const { getRootProps, getInputProps, isDragActive } = useDropzone({ 
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    multiple: false
  });
  
  return (
    <Box>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}
      
      <Paper
        {...getRootProps()}
        elevation={1}
        sx={{
          p: 2,
          textAlign: 'center',
          cursor: 'pointer',
          backgroundColor: isDragActive ? 'rgba(123, 104, 238, 0.1)' : 'background.default',
          border: isDragActive ? '2px dashed #7b68ee' : '2px dashed #3d3d3d',
          borderRadius: 2,
          transition: 'all 0.2s',
          '&:hover': {
            backgroundColor: 'rgba(123, 104, 238, 0.05)',
            borderColor: 'primary.main',
          },
        }}
      >
        <input {...getInputProps()} />
        <CloudUploadIcon sx={{ fontSize: 36, color: 'primary.main', mb: 1 }} />
        <Typography variant="body1" gutterBottom>
          {isDragActive ? 'Drop the file here' : 'Upload Document'}
        </Typography>
        <Typography variant="caption" color="text.secondary">
          Drag & drop or click to select â€¢ PDF, DOCX, TXT
        </Typography>
      </Paper>
      
      {isUploading && (
        <Box sx={{ mt: 2 }}>
          <Typography variant="body2" color="textSecondary" gutterBottom>
            Uploading and processing file...
          </Typography>
          <LinearProgress variant="determinate" value={progress} />
        </Box>
      )}
    </Box>
  );
};

export default FileUpload;
