import React from 'react';
import { 
  Box, 
  Typography, 
  List, 
  ListItem, 
  ListItemIcon, 
  ListItemText,
  Paper
} from '@mui/material';
import DescriptionIcon from '@mui/icons-material/Description';

const FileList = ({ files }) => {
  if (!files || files.length === 0) {
    return null;
  }
  
  return (
    <Box>
      <List sx={{ p: 0 }}>
        {files.map((file) => (
          <Paper
            key={file.file_id}
            elevation={1}
            sx={{
              mb: 1,
              backgroundColor: 'background.default',
              borderRadius: 2,
              overflow: 'hidden',
              transition: 'all 0.2s',
              '&:hover': {
                backgroundColor: 'rgba(123, 104, 238, 0.05)',
              },
            }}
          >
            <ListItem>
              <ListItemIcon>
                <DescriptionIcon color="primary" />
              </ListItemIcon>
              <ListItemText 
                primary={
                  <Typography variant="body2" noWrap title={file.filename}>
                    {file.filename}
                  </Typography>
                }
                secondary={
                  <Typography variant="caption" color="text.secondary">
                    {`${file.chunk_count} chunks processed`}
                  </Typography>
                }
              />
            </ListItem>
          </Paper>
        ))}
      </List>
    </Box>
  );
};

export default FileList;
