import React, { useState } from 'react';
import { 
  Box, 
  TextField, 
  Button,
  InputAdornment
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

const QueryInput = ({ onSubmit, disabled }) => {
  const [query, setQuery] = useState('');
  
  const handleSubmit = (e) => {
    e.preventDefault();
    if (query.trim()) {
      onSubmit(query.trim());
    }
  };
  
  return (
    <Box component="form" onSubmit={handleSubmit}>
      <TextField
        fullWidth
        variant="outlined"
        label="Ask a question"
        placeholder="e.g., What are the main findings in this paper? or What's the latest research on climate change?"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        disabled={disabled}
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <SearchIcon />
            </InputAdornment>
          ),
        }}
        sx={{ mb: 2 }}
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        disabled={!query.trim() || disabled}
        sx={{ minWidth: 120 }}
      >
        Search
      </Button>
    </Box>
  );
};

export default QueryInput;
