import React, { useState } from 'react';
import { 
  Box, 
  TextField, 
  Button,
  InputAdornment
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const QueryInput = ({ onSubmit, disabled, placeholder, fullWidth }) => {
  const [query, setQuery] = useState('');
  
  const handleSubmit = (e) => {
    e.preventDefault();
    if (query.trim()) {
      onSubmit(query.trim());
      setQuery(''); // Clear input after sending
    }
  };
  
  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', width: '100%' }}>
      <TextField
        fullWidth
        variant="outlined"
        placeholder={placeholder || "Ask a question about your documents or any research topic..."}
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        disabled={disabled}
        size="medium"
        sx={{ 
          mr: 1,
          '& .MuiOutlinedInput-root': {
            borderRadius: 2,
            backgroundColor: 'background.default',
          }
        }}
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        disabled={!query.trim() || disabled}
        sx={{ 
          borderRadius: 2,
          minWidth: 'auto',
          px: 2
        }}
        endIcon={<SendIcon />}
      >
        Send
      </Button>
    </Box>
  );
};

export default QueryInput;
